import React, { useState, useEffect, useCallback } from "react";
import toast from "react-hot-toast";
import { marketStatus, riskManagement, formatters } from "../utils/tradingEngine";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

export default function PaperTrading() {
  const [paperCapital] = useState(1000000);
  const [currentCapital, setCurrentCapital] = useState(1000000);
  const [trades, setTrades] = useState([]);
  const [showNewTrade, setShowNewTrade] = useState(false);
  const [newTrade, setNewTrade] = useState({
    symbol: "INFY",
    tradingStyle: "SWING",
    quantity: 10,
    entry_price: 1500,
    exit_type: "target",
  });
  const [marketOpen, setMarketOpen] = useState(false);
  const [marketStatus, setMarketStatusInfo] = useState(null);
  const [showDisciplineScore, setShowDisciplineScore] = useState(false);

  const [portfolioValue, setPortfolioValue] = useState([
    { date: "Day 1", value: 1000000 },
  ]);

  useEffect(() => {
    checkMarketStatus();
    calculateTotalValue();
    const interval = setInterval(checkMarketStatus, 60000);
    return () => clearInterval(interval);
  }, [trades, currentCapital]);

  const checkMarketStatus = () => {
    const status = marketStatus.getMarketStatus();
    setMarketOpen(status.isOpen);
    setMarketStatusInfo(status);
  };

  const calculateTotalValue = () => {
    const investedValue = trades.reduce((sum, trade) => {
      return sum + trade.quantity * trade.entry_price;
    }, 0);

    const totalValue = currentCapital + investedValue;
    updatePortfolioValue(totalValue);
  };

  const updatePortfolioValue = (value) => {
    setPortfolioValue((prev) => [
      ...prev,
      { date: `Day ${prev.length + 1}`, value },
    ]);
  };

  const handleAddTrade = async (e) => {
    e.preventDefault();

    // Market validation
    if (!marketOpen) {
      toast.error(
        `Market is closed. Open: 9:15 AM - 3:30 PM (IST). Time: ${marketStatus.getTimeUntilMarketOpen()}`
      );
      return;
    }

    // Capital validation
    const tradeValue = newTrade.quantity * newTrade.entry_price;
    if (tradeValue > currentCapital) {
      toast.error("Insufficient capital for this trade");
      return;
    }

    // Risk management validation (1% rule)
    const targetPrice = parseFloat(newTrade.target_price || newTrade.entry_price * 1.02);
    const stopPrice = parseFloat(newTrade.stop_price || newTrade.entry_price * 0.98);

    const riskPerTrade = tradeValue * 0.01; // 1% of capital
    const actualRisk = tradeValue - (tradeValue * (stopPrice / newTrade.entry_price));

    if (actualRisk > riskPerTrade) {
      toast.error(
        `Risk too high. Max risk: â‚¹${formatters.formatCurrency(riskPerTrade)}. Adjust stop loss.`
      );
      return;
    }

    // Simulate slippage (0.05% to 0.1%)
    const slippagePercent = 0.0005 + Math.random() * 0.0005;
    const executionPrice = newTrade.entry_price * (1 + slippagePercent);

    // Simulate execution delay
    const executionDelay = 500 + Math.random() * 1500;

    setTimeout(() => {
      const trade = {
        id: Date.now(),
        ...newTrade,
        entry_price: executionPrice,
        target_price: targetPrice,
        stop_price: stopPrice,
        entry_time: new Date().toLocaleTimeString(),
        entry_date: new Date().toLocaleDateString(),
        status: "open",
        entry_value: newTrade.quantity * executionPrice,
        pnl: 0,
        pnlPercent: 0,
        riskReward: (targetPrice - executionPrice) / (executionPrice - stopPrice),
        discipline: {
          timeHeld: 0,
          targetHit: false,
          stopHit: false,
        },
      };

      setTrades([...trades, trade]);
      setCurrentCapital(currentCapital - trade.entry_value);
      setNewTrade({
        symbol: "INFY",
        tradingStyle: "SWING",
        quantity: 10,
        entry_price: 1500,
        exit_type: "target",
      });
      setShowNewTrade(false);
      toast.success(`Trade executed at â‚¹${executionPrice.toFixed(2)} (slippage: â‚¹${(executionPrice - newTrade.entry_price).toFixed(2)})`);
    }, executionDelay);
  };

  const handleCloseTrade = (tradeId, exitPrice, exitType = "manual") => {
    const trade = trades.find((t) => t.id === tradeId);
    if (!trade) return;

    // Simulate slippage on exit (0.05% to 0.1%)
    const slippagePercent = 0.0005 + Math.random() * 0.0005;
    const exitSlippage = exitPrice * slippagePercent;
    const finalExitPrice = exitType === "manual" ? exitPrice + exitSlippage : exitPrice;

    // Calculate commission (0.05% per side = 0.1% total)
    const commission = (trade.entry_value * 0.001) + (trade.quantity * finalExitPrice * 0.001);

    const exitValue = trade.quantity * finalExitPrice - commission;
    const pnl = exitValue - trade.entry_value;
    const pnlPercent = (pnl / trade.entry_value) * 100;

    // Calculate time held
    const now = new Date();
    const entryTime = new Date();
    const timeHeld = Math.round((now - entryTime) / (1000 * 60)); // minutes

    const closedTrade = {
      ...trade,
      exit_price: finalExitPrice,
      exit_time: new Date().toLocaleTimeString(),
      exit_date: new Date().toLocaleDateString(),
      exit_type: exitType,
      status: "closed",
      pnl: Math.round(pnl * 100) / 100,
      pnlPercent: Math.round(pnlPercent * 100) / 100,
      commission: Math.round(commission * 100) / 100,
      timeHeld: timeHeld,
      discipline: {
        ...trade.discipline,
        targetHit: exitType === "target",
        stopHit: exitType === "stop",
      },
    };

    setTrades(
      trades.map((t) => (t.id === tradeId ? closedTrade : t))
    );

    setCurrentCapital(currentCapital + exitValue);
    const successMsg = `Trade closed with ${pnlPercent > 0 ? "âœ… profit" : "âŒ loss"}: ${pnlPercent.toFixed(2)}% (${exitType}). Commission: â‚¹${commission.toFixed(2)}`;
    toast.success(successMsg);
  };

  const getTotalReturn = () => {
    const lastValue = portfolioValue[portfolioValue.length - 1]?.value || paperCapital;
    return ((lastValue - paperCapital) / paperCapital) * 100;
  };

  const getTotalPnL = () => {
    const lastValue = portfolioValue[portfolioValue.length - 1]?.value || paperCapital;
    return lastValue - paperCapital;
  };

  const getWinRate = () => {
    const closedTrades = trades.filter((t) => t.status === "closed");
    if (closedTrades.length === 0) return 0;
    const winners = closedTrades.filter((t) => t.pnl > 0).length;
    return ((winners / closedTrades.length) * 100).toFixed(1);
  };

  const calculateDisciplineScore = () => {
    const closedTrades = trades.filter((t) => t.status === "closed");
    if (closedTrades.length === 0) return 100;

    let score = 100;

    // Penalty for stops not hit (cutting winners early or letting losers run)
    const targetHits = closedTrades.filter((t) => t.discipline?.targetHit).length;
    const stopHits = closedTrades.filter((t) => t.discipline?.stopHit).length;

    if (closedTrades.length > 0) {
      const stopDiscipline = (stopHits / closedTrades.length) * 100;
      if (stopDiscipline < 50) score -= 15; // Penalty if not taking stops
    }

    // Reward for following target discipline
    if (targetHits > stopHits && targetHits > closedTrades.length * 0.5) {
      score += 10;
    }

    // Penalty for average loss > average win (poor risk/reward)
    const avgWin = closedTrades.filter((t) => t.pnl > 0).reduce((sum, t) => sum + t.pnl, 0) / (closedTrades.filter((t) => t.pnl > 0).length || 1);
    const avgLoss = Math.abs(closedTrades.filter((t) => t.pnl < 0).reduce((sum, t) => sum + t.pnl, 0) / (closedTrades.filter((t) => t.pnl < 0).length || 1));

    if (avgLoss > avgWin * 1.5 && closedTrades.filter((t) => t.pnl < 0).length > 0) {
      score -= 10;
    }

    return Math.max(0, Math.min(100, score));
  };

  return (
    <div className="space-y-6">
      {/* Market Status Banner */}
      {marketStatus && (
        <div
          className={`rounded-lg shadow-md p-4 text-white ${
            marketOpen ? "bg-green-600" : "bg-red-600"
          }`}
        >
          <div className="flex justify-between items-center">
            <div className="text-lg font-semibold">
              {marketOpen ? "ðŸŸ¢ Market Open - Live Trading" : "ðŸ”’ Market Closed - Simulation Mode"}
            </div>
            <div className="text-sm">
              {marketOpen
                ? "Open: 9:15 AM - 3:30 PM (IST)"
                : `Opens at: ${marketStatus.timeUntilOpen}`}
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-900">ðŸ“ Paper Trading</h1>
        <p className="text-gray-600 mt-2">Practice trading with virtual capital - no real money involved</p>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-sm text-gray-600 mb-1">Paper Capital</p>
          <p className="text-3xl font-bold text-gray-900">â‚¹{paperCapital.toLocaleString()}</p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-sm text-gray-600 mb-1">Current Value</p>
          <p className="text-3xl font-bold text-primary">
            â‚¹{(currentCapital + trades.reduce((sum, t) => sum + (t.status === "open" ? t.entry_value : 0), 0)).toLocaleString()}
          </p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-sm text-gray-600 mb-1">Total P&L</p>
          <p className={`text-3xl font-bold ${getTotalPnL() >= 0 ? "text-green-600" : "text-red-600"}`}>
            {getTotalPnL() >= 0 ? "+" : ""}â‚¹{getTotalPnL().toFixed(0)}
          </p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-sm text-gray-600 mb-1">Total Return</p>
          <p className={`text-3xl font-bold ${getTotalReturn() >= 0 ? "text-green-600" : "text-red-600"}`}>
            {getTotalReturn() >= 0 ? "+" : ""}{getTotalReturn().toFixed(2)}%
          </p>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <p className="text-sm text-gray-600 mb-1">Win Rate</p>
          <p className="text-3xl font-bold text-primary">{getWinRate()}%</p>
        </div>
      </div>

      {/* Actions */}
      <div className="flex gap-2">
        <button
          onClick={() => setShowNewTrade(!showNewTrade)}
          className="bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 px-6 rounded-lg hover:shadow-lg transition"
        >
          âž• New Trade
        </button>
        <button
          onClick={() => {
            setTrades([]);
            setCurrentCapital(paperCapital);
            setPortfolioValue([{ date: "Day 1", value: paperCapital }]);
            toast.success("Paper trading reset!");
          }}
          className="bg-gray-400 text-white font-semibold py-2 px-6 rounded-lg hover:shadow-lg transition"
        >
          ðŸ”„ Reset
        </button>
      </div>

      {/* New Trade Form */}
      {showNewTrade && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <form onSubmit={handleAddTrade} className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Symbol</label>
              <input
                type="text"
                value={newTrade.symbol}
                onChange={(e) => setNewTrade({ ...newTrade, symbol: e.target.value.toUpperCase() })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Quantity</label>
              <input
                type="number"
                value={newTrade.quantity}
                onChange={(e) => setNewTrade({ ...newTrade, quantity: parseInt(e.target.value) })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Entry Price (â‚¹)</label>
              <input
                type="number"
                step="0.01"
                value={newTrade.entry_price}
                onChange={(e) => setNewTrade({ ...newTrade, entry_price: parseFloat(e.target.value) })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Stop Loss (â‚¹)</label>
              <input
                type="number"
                step="0.01"
                value={newTrade.stop_price || (newTrade.entry_price * 0.98).toFixed(2)}
                onChange={(e) => setNewTrade({ ...newTrade, stop_price: parseFloat(e.target.value) })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Target Price (â‚¹)</label>
              <input
                type="number"
                step="0.01"
                value={newTrade.target_price || (newTrade.entry_price * 1.02).toFixed(2)}
                onChange={(e) => setNewTrade({ ...newTrade, target_price: parseFloat(e.target.value) })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
              />
            </div>
            <div className="flex items-end gap-2 md:col-span-5">
              <button
                type="submit"
                className="flex-1 bg-green-600 text-white font-semibold py-2 rounded-lg hover:shadow-lg transition"
              >
                Open Trade
              </button>
              <button
                type="button"
                onClick={() => setShowNewTrade(false)}
                className="flex-1 bg-gray-300 text-gray-700 font-semibold py-2 rounded-lg hover:shadow-lg transition"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Portfolio Performance Chart */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Portfolio Performance</h3>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={portfolioValue}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip formatter={(value) => `â‚¹${value.toLocaleString()}`} />
            <Legend />
            <Line
              type="monotone"
              dataKey="value"
              stroke="#0088FE"
              dot={false}
              name="Portfolio Value"
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* Open Trades */}
      {trades.filter((t) => t.status === "open").length > 0 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">ðŸ“Š Open Trades</h3>
          <div className="space-y-3">
            {trades
              .filter((t) => t.status === "open")
              .map((trade) => (
                <div key={trade.id} className="border rounded-lg p-4 hover:bg-gray-50">
                  <div className="flex justify-between items-start mb-3">
                    <div>
                      <h4 className="text-lg font-bold text-gray-900">{trade.symbol}</h4>
                      <p className="text-sm text-gray-600">Opened at {trade.entry_time}</p>
                    </div>
                    <span className="text-2xl font-bold text-green-600">{trade.tradingStyle}</span>
                  </div>

                  <div className="grid grid-cols-2 md:grid-cols-5 gap-3 mb-4">
                    <div>
                      <p className="text-xs text-gray-600">Quantity</p>
                      <p className="font-semibold">{trade.quantity}</p>
                    </div>
                    <div>
                      <p className="text-xs text-gray-600">Entry</p>
                      <p className="font-semibold">â‚¹{trade.entry_price.toFixed(2)}</p>
                    </div>
                    <div>
                      <p className="text-xs text-gray-600">Stop Loss</p>
                      <p className="font-semibold text-red-600">â‚¹{trade.stop_price?.toFixed(2) || "N/A"}</p>
                    </div>
                    <div>
                      <p className="text-xs text-gray-600">Target</p>
                      <p className="font-semibold text-green-600">â‚¹{trade.target_price?.toFixed(2) || "N/A"}</p>
                    </div>
                    <div>
                      <p className="text-xs text-gray-600">Risk/Reward</p>
                      <p className="font-semibold text-blue-600">{trade.riskReward?.toFixed(2)}:1</p>
                    </div>
                  </div>

                  <div className="flex gap-2 flex-wrap">
                    <button
                      onClick={() => handleCloseTrade(trade.id, trade.target_price, "target")}
                      className="flex-1 min-w-[120px] bg-green-600 text-white font-semibold py-2 px-4 rounded-lg hover:shadow-lg transition text-sm"
                    >
                      âœ… Close at Target
                    </button>
                    <button
                      onClick={() => handleCloseTrade(trade.id, trade.stop_price, "stop")}
                      className="flex-1 min-w-[120px] bg-red-600 text-white font-semibold py-2 px-4 rounded-lg hover:shadow-lg transition text-sm"
                    >
                      âŒ Hit Stop Loss
                    </button>
                    <input
                      type="number"
                      step="0.01"
                      placeholder="Manual exit"
                      id={`exit-${trade.id}`}
                      className="flex-1 min-w-[120px] px-3 py-2 border border-gray-300 rounded-lg text-sm"
                    />
                    <button
                      onClick={() => {
                        const exitPrice = parseFloat(document.getElementById(`exit-${trade.id}`).value);
                        if (exitPrice > 0) {
                          handleCloseTrade(trade.id, exitPrice, "manual");
                        } else {
                          toast.error("Enter a valid exit price");
                        }
                      }}
                      className="bg-blue-600 text-white font-semibold py-2 px-4 rounded-lg hover:shadow-lg transition text-sm"
                    >
                      Manual Exit
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}

      {/* Closed Trades */}
      {trades.filter((t) => t.status === "closed").length > 0 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">âœ… Closed Trades</h3>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Symbol</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Qty</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Entry</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Exit</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Type</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">P&L</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Return %</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Commission</th>
                  <th className="px-4 py-2 text-left font-semibold text-gray-700">Time</th>
                </tr>
              </thead>
              <tbody className="divide-y">
                {trades
                  .filter((t) => t.status === "closed")
                  .map((trade) => (
                    <tr key={trade.id} className="hover:bg-gray-50">
                      <td className="px-4 py-3 font-semibold">{trade.symbol}</td>
                      <td className="px-4 py-3">{trade.quantity}</td>
                      <td className="px-4 py-3">â‚¹{trade.entry_price.toFixed(2)}</td>
                      <td className="px-4 py-3">â‚¹{trade.exit_price?.toFixed(2)}</td>
                      <td className="px-4 py-3 text-xs">
                        {trade.exit_type === "target" && "ðŸŽ¯ Target"}
                        {trade.exit_type === "stop" && "ðŸ›‘ Stop"}
                        {trade.exit_type === "manual" && "âœ‹ Manual"}
                      </td>
                      <td className={`px-4 py-3 font-semibold ${trade.pnl >= 0 ? "text-green-600" : "text-red-600"}`}>
                        {trade.pnl >= 0 ? "+" : ""}â‚¹{trade.pnl.toFixed(0)}
                      </td>
                      <td className={`px-4 py-3 font-semibold ${trade.pnlPercent >= 0 ? "text-green-600" : "text-red-600"}`}>
                        {trade.pnlPercent >= 0 ? "+" : ""}{trade.pnlPercent.toFixed(2)}%
                      </td>
                      <td className="px-4 py-3 text-red-600">â‚¹{trade.commission?.toFixed(0) || "0"}</td>
                      <td className="px-4 py-3 text-xs">{trade.timeHeld || 0} min</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>

          {/* Discipline Score */}
          <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg">
            <div className="flex justify-between items-center">
              <div>
                <p className="text-sm text-gray-600 mb-1">Discipline Score</p>
                <p className="text-3xl font-bold text-blue-600">{calculateDisciplineScore()}%</p>
              </div>
              <button
                onClick={() => setShowDisciplineScore(!showDisciplineScore)}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:shadow-lg"
              >
                {showDisciplineScore ? "Hide Details" : "Show Details"}
              </button>
            </div>

            {showDisciplineScore && (
              <div className="mt-4 space-y-2 text-sm text-gray-700">
                <p>âœ… <strong>Discipline Metrics:</strong></p>
                <p>â€¢ Stop Loss Hit Rate: {((trades.filter((t) => t.status === "closed" && t.discipline?.stopHit).length / trades.filter((t) => t.status === "closed").length * 100) || 0).toFixed(0)}%</p>
                <p>â€¢ Target Achievement Rate: {((trades.filter((t) => t.status === "closed" && t.discipline?.targetHit).length / trades.filter((t) => t.status === "closed").length * 100) || 0).toFixed(0)}%</p>
                <p>â€¢ Average Win: â‚¹{(trades.filter((t) => t.status === "closed" && t.pnl > 0).reduce((sum, t) => sum + t.pnl, 0) / (trades.filter((t) => t.status === "closed" && t.pnl > 0).length || 1)).toFixed(0)}</p>
                <p>â€¢ Average Loss: â‚¹{(Math.abs(trades.filter((t) => t.status === "closed" && t.pnl < 0).reduce((sum, t) => sum + t.pnl, 0)) / (trades.filter((t) => t.status === "closed" && t.pnl < 0).length || 1)).toFixed(0)}</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Empty State */}
      {trades.length === 0 && (
        <div className="bg-white rounded-lg shadow-md p-12 text-center">
          <p className="text-gray-600 text-lg">No trades yet</p>
          <p className="text-gray-400">Open your first paper trade to practice trading strategies</p>
        </div>
      )}
    </div>
  );
}
