import React from "react";
import { useAuthStore } from "../store";
import { useNavigate } from "react-router-dom";

export default function Header({ onMenuClick }) {
  const { user, logout } = useAuthStore((s) => ({
    user: s.user,
    logout: s.logout,
  }));
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <header className="bg-white shadow-md">
      <div className="max-w-full px-6 py-4 flex justify-between items-center">
        <button
          onClick={onMenuClick}
          className="lg:hidden text-2xl text-gray-700 hover:text-primary transition"
        >
          â˜°
        </button>

        <div className="flex-1 text-center lg:text-left">
          <h2 className="text-xl font-bold text-gray-900">
            Welcome, {user?.username || "Trader"}! ðŸ“ˆ
          </h2>
          <p className="text-sm text-gray-500">
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })}
          </p>
        </div>

        <div className="flex items-center gap-4">
          <button className="text-gray-600 hover:text-primary transition text-2xl">
            ðŸ””
          </button>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition font-medium text-sm"
          >
            Logout
          </button>
        </div>
      </div>
    </header>
  );
}
