import React, { useState, useEffect } from "react";
import { intradayApi, recommendationApi } from "../api/endpoints";
import toast from "react-hot-toast";
import { marketStatus, predictionControl, riskManagement, formatters } from "../utils/tradingEngine";

export default function Intraday() {
  const [symbol, setSymbol] = useState("INFY");
  const [tradingStyle, setTradingStyle] = useState("INTRADAY");
  const [capital, setCapital] = useState(50000);
  const [signal, setSignal] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [openTrades, setOpenTrades] = useState([]);
  const [marketOpen, setMarketOpen] = useState(false);
  const [marketStatusInfo, setMarketStatusInfo] = useState(null);
  const [predictionMode, setPredictionMode] = useState(null);
  const [riskAssessment, setRiskAssessment] = useState(null);
  const [sessionTime, setSessionTime] = useState("OPENING");

  useEffect(() => {
    const updateMarketStatus = () => {
      const isOpen = marketStatus.isMarketOpen();
      const status = marketStatus.getMarketStatus();
      setMarketOpen(isOpen);
      setMarketStatusInfo(status);
      setPredictionMode(predictionControl.getPredictionMode(isOpen));

      // Also update session time
      const now = new Date();
      const hour = now.getHours();
      if (hour >= 9 && hour < 12) {
        setSessionTime("OPENING");
      } else if (hour >= 12 && hour < 15) {
        setSessionTime("MID_DAY");
      } else if (hour >= 15 && hour < 16) {
        setSessionTime("CLOSING");
      } else {
        setSessionTime("CLOSED");
      }
    };

    updateMarketStatus();
    const timer = setInterval(updateMarketStatus, 60000);
    return () => clearInterval(timer);
  }, []);

  const handleGenerateSignal = async (e) => {
    e.preventDefault();

    // Validate market is open
    if (!marketOpen) {
      toast.error("â° Market is closed. Intraday signals available during market hours (9:15 AM - 3:30 PM)");
      return;
    }

    setIsLoading(true);

    try {
      const response = await recommendationApi.generateRecommendation({
        stock_symbol: symbol,
        trading_style: tradingStyle,
        capital,
      });

      const signalData = response.data;
      setSignal(signalData);

      // Calculate risk assessment
      if (signalData.entry_price && signalData.stop_loss) {
        const pnlData = riskManagement.calculatePnL(
          capital,
          signalData.entry_price,
          [signalData.target_1, signalData.target_2, signalData.target_3],
          signalData.stop_loss
        );
        setRiskAssessment(pnlData);

        // Check for risk warnings
        const warnings = riskManagement.assessRisk(
          capital,
          signalData.entry_price,
          signalData.stop_loss,
          [signalData.target_1]
        );

        if (warnings.warnings.length > 0) {
          warnings.warnings.forEach(w => toast.error(w));
        }
      }

      toast.success("âœ… Intraday signal generated - Review risk details before executing!");
    } catch (error) {
      toast.error("Failed to generate signal");
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExecuteTrade = async () => {
    if (!signal) return;

    try {
      await recommendationApi.executeRecommendation(signal.id);
      setOpenTrades([...openTrades, { ...signal, entry_time: new Date() }]);
      toast.success("Trade executed!");
      setSignal(null);
    } catch (error) {
      toast.error("Failed to execute trade");
      console.error(error);
    }
  };

  const getSessionColor = (session) => {
    switch (session) {
      case "OPENING":
        return "bg-orange-100 text-orange-800";
      case "MID_DAY":
        return "bg-blue-100 text-blue-800";
      case "CLOSING":
        return "bg-red-100 text-red-800";
      case "CLOSED":
        return "bg-gray-100 text-gray-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getSignalColor = (signalType) => {
    switch (signalType) {
      case "BUY":
        return "text-green-600 bg-green-50 border-green-500";
      case "SELL":
        return "text-red-600 bg-red-50 border-red-500";
      case "HOLD":
        return "text-yellow-600 bg-yellow-50 border-yellow-500";
      default:
        return "text-gray-600 bg-gray-50 border-gray-500";
    }
  };

  return (
    <div className="space-y-6">
      {/* Market Status Banner */}
      {marketStatusInfo && (
        <div className={`${marketStatusInfo.color} rounded-lg p-4 border border-gray-200`}>
          <div className="flex justify-between items-center">
            <div>
              <p className="font-semibold text-gray-900">{marketStatusInfo.label}</p>
              <p className="text-sm text-gray-600 mt-1">
                {marketOpen ? "âœ… Intraday trading available" : "ğŸ”’ Trading disabled - market closed"}
              </p>
            </div>
            <span className="text-3xl">
              {marketOpen ? 'ğŸŸ¢' : 'ğŸ”’'}
            </span>
          </div>
        </div>
      )}

      {/* Market Closed Warning */}
      {!marketOpen && (
        <div className="bg-gray-50 border-l-4 border-gray-400 p-4 rounded">
          <div className="flex items-start">
            <span className="text-2xl mr-3">ğŸ“…</span>
            <div>
              <h3 className="font-bold text-gray-900">Market Closed - Analysis Mode</h3>
              <p className="text-sm text-gray-700 mt-1">Intraday signals are disabled when the market is closed.</p>
              {(() => {
                const timeLeft = marketStatus.getTimeUntilMarketOpen();
                return (
                  <p className="text-xs text-blue-600 font-semibold mt-2">
                    â° Market opens in {timeLeft.hoursUntil}h {timeLeft.minutesUntil}m (9:15 AM IST)
                  </p>
                );
              })()}
            </div>
          </div>
        </div>
      )}
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">âš¡ Intraday Trading</h1>
            <p className="text-gray-600 mt-2">High-frequency trading signals for today</p>
          </div>
          <div className={`px-4 py-2 rounded-lg font-semibold text-sm ${getSessionColor(sessionTime)}`}>
            {sessionTime === "OPENING" && "ğŸ“ˆ Morning Session"}
            {sessionTime === "MID_DAY" && "ğŸ“Š Mid Day"}
            {sessionTime === "CLOSING" && "ğŸ“‰ Closing Session"}
            {sessionTime === "CLOSED" && "ğŸ”’ Market Closed"}
          </div>
        </div>
      </div>

      {/* Signal Generator */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleGenerateSignal} className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Stock Symbol</label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              placeholder="e.g., INFY"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Capital (â‚¹)</label>
            <input
              type="number"
              value={capital}
              onChange={(e) => setCapital(parseInt(e.target.value))}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Trading Style</label>
            <select
              value={tradingStyle}
              onChange={(e) => setTradingStyle(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            >
              <option value="INTRADAY">Intraday</option>
              <option value="SWING">Swing</option>
              <option value="POSITIONAL">Positional</option>
            </select>
          </div>

          <div className="flex items-end">
            <button
              type="submit"
              disabled={isLoading || sessionTime === "CLOSED"}
              className="w-full bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              {isLoading ? "Generating..." : "Generate Signal"}
            </button>
          </div>
        </form>
      </div>

      {/* Current Signal */}
      {signal && (
        <div className={`rounded-lg shadow-md p-8 border-l-4 ${getSignalColor(signal.signal)}`}>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
            <div>
              <p className="text-sm text-gray-600 mb-2">Trading Signal</p>
              <p className="text-5xl font-bold">{signal.signal}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600 mb-2">Entry Price</p>
              <p className="text-4xl font-bold">â‚¹{signal.entry_price}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600 mb-2">Stop Loss</p>
              <p className="text-4xl font-bold">â‚¹{signal.stop_loss}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600 mb-2">Win Probability</p>
              <p className="text-4xl font-bold">{signal.win_probability}%</p>
            </div>
          </div>

          {/* Risk Assessment */}
          {riskAssessment && riskAssessment.valid && (
            <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg p-4 mb-6">
              <h3 className="font-bold text-gray-900 mb-3">ğŸ’° Position Sizing & Risk (1% Rule)</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-3 text-sm">
                <div>
                  <p className="text-xs text-gray-600">Max Risk (1%)</p>
                  <p className="font-bold text-green-600">{formatters.formatCurrency(riskAssessment.maxRisk)}</p>
                </div>
                <div>
                  <p className="text-xs text-gray-600">Position Size</p>
                  <p className="font-bold text-blue-600">{riskAssessment.position} units</p>
                </div>
                <div>
                  <p className="text-xs text-gray-600">Actual Risk</p>
                  <p className="font-bold text-orange-600">{formatters.formatCurrency(riskAssessment.riskAmount)}</p>
                </div>
                <div>
                  <p className="text-xs text-gray-600">Risk/Reward</p>
                  <p className="font-bold text-purple-600">1:{riskManagement.calculateRiskReward(signal.entry_price, signal.target_1, signal.stop_loss)}</p>
                </div>
              </div>
            </div>
          )}

          {/* Risk Warnings */}
          {riskAssessment && riskAssessment.valid && (() => {
            const warnings = riskManagement.assessRisk(
              capital,
              signal.entry_price,
              signal.stop_loss,
              [signal.target_1]
            );
            return warnings.warnings.length > 0 ? (
              <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6 rounded">
                <h4 className="font-bold text-yellow-900 mb-2">âš ï¸ Risk Alerts</h4>
                <ul className="text-sm text-yellow-800 space-y-1">
                  {warnings.warnings.map((w, i) => <li key={i}>{w}</li>)}
                </ul>
              </div>
            ) : null;
          })()}

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6 pb-6 border-b">
            <div>
              <p className="text-sm text-gray-600">Target 1</p>
              <p className="text-2xl font-bold text-green-600">â‚¹{signal.target_1}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Target 2</p>
              <p className="text-2xl font-bold text-green-600">â‚¹{signal.target_2}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Target 3</p>
              <p className="text-2xl font-bold text-green-600">â‚¹{signal.target_3}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Target 4</p>
              <p className="text-2xl font-bold text-green-600">â‚¹{signal.target_4}</p>
            </div>
          </div>

          <div className="flex gap-4">
            <button
              onClick={handleExecuteTrade}
              disabled={!marketOpen}
              className="flex-1 bg-green-600 text-white font-semibold py-3 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              âœ… Execute Trade
            </button>
            <button
              onClick={() => setSignal(null)}
              className="flex-1 bg-gray-300 text-gray-700 font-semibold py-3 rounded-lg hover:shadow-lg transition"
            >
              âŒ Discard
            </button>
          </div>
        </div>
      )}

      {/* Open Trades */}
      {openTrades.length > 0 && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4">ğŸ“Š Open Intraday Trades</h3>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Symbol</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Signal</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Entry</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Stop Loss</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Exit Time</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold text-gray-700">Action</th>
                </tr>
              </thead>
              <tbody className="divide-y">
                {openTrades.map((trade, idx) => (
                  <tr key={idx} className="hover:bg-gray-50">
                    <td className="px-4 py-3 font-semibold text-gray-900">{trade.stock?.symbol}</td>
                    <td className={`px-4 py-3 font-bold ${trade.signal === "BUY" ? "text-green-600" : "text-red-600"}`}>
                      {trade.signal}
                    </td>
                    <td className="px-4 py-3 text-gray-700">â‚¹{trade.entry_price}</td>
                    <td className="px-4 py-3 text-gray-700">â‚¹{trade.stop_loss}</td>
                    <td className="px-4 py-3 text-gray-700">
                      {trade.valid_until_session || "End of Day"}
                    </td>
                    <td className="px-4 py-3">
                      <button className="text-red-600 hover:text-red-800 font-semibold">Close</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Market Closed Notice */}
      {sessionTime === "CLOSED" && (
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
          <p className="text-yellow-800 font-semibold text-lg">ğŸ”’ Market is Closed</p>
          <p className="text-yellow-700 mt-2">Intraday signals are available during market hours (9:15 AM - 3:30 PM)</p>
        </div>
      )}

      {/* Empty State */}
      {!signal && openTrades.length === 0 && (
        <div className="bg-white rounded-lg shadow-md p-12 text-center">
          <p className="text-gray-600 text-lg">Generate a signal to see intraday trading recommendations</p>
          <p className="text-gray-400">Signals are optimized for quick entries and exits within the same trading day</p>
        </div>
      )}
    </div>
  );
}
