import React, { useState, useEffect } from "react";
import { intradayApi, recommendationApi } from "../api/endpoints";
import { useTradingStore } from "../store";
import toast from "react-hot-toast";
import { riskManagement, marketStatus, formatters } from "../utils/tradingEngine";
import { Card, CardHeader, CardTitle, CardContent } from "../components/ui/Card";
import { Button } from "../components/ui/Button";
import { Badge, Stat, Progress } from "../components/ui/Common";
import { Tabs } from "../components/ui/Layouts";

export default function Signals() {
  const [signals, setSignals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("all");
  const [selectedMode] = useTradingStore((s) => [s.selectedMode]);
  const [marketOpen, setMarketOpen] = useState(false);
  const [defaultCapital, setDefaultCapital] = useState(100000);
  const [selectedSignal, setSelectedSignal] = useState(null);

  useEffect(() => {
    fetchSignals();
  }, [activeTab]);

  useEffect(() => {
    const updateMarketStatus = () => {
      setMarketOpen(marketStatus.isMarketOpen());
    };

    updateMarketStatus();
    const timer = setInterval(updateMarketStatus, 60000);
    return () => clearInterval(timer);
  }, []);

  const fetchSignals = async () => {
    setIsLoading(true);
    try {
      let response;
      try {
        response =
          activeTab === "intraday"
            ? await intradayApi.getIntradaySignals()
            : await recommendationApi.getActiveSignals();
      } catch (err) {
        console.error("API Error fetching signals:", err);

        if (err.response?.status === 404) {
          toast.error("Signals not found. Generate analysis first.");
        } else if (err.response?.status === 401) {
          toast.error("Session expired. Please login again.");
        }

        response = { data: [] };
      }

      const signalList = Array.isArray(response.data)
        ? response.data
        : response.data?.results || [];

      const enhancedSignals = (signalList || []).map((signal) => {
        const entry = Number(signal.entry_price) || 0;
        const target = Number(signal.target_1) || 0;
        const stop = Number(signal.stop_loss) || 0;
        const hasValidPrices = entry && target && stop;

        return {
          ...signal,
          entry_price: entry,
          target_1: target,
          stop_loss: stop,
          market_status: marketOpen ? "LIVE" : "HISTORICAL",
          riskReward: hasValidPrices
            ? riskManagement.calculateRiskReward(entry, target, stop)
            : 0,
          expectedProfit: entry && target
            ? ((target - entry) / entry * 100).toFixed(2)
            : "0.00",
        };
      });

      setSignals(enhancedSignals);
      if (enhancedSignals.length === 0) {
        toast.info("No signals available. Generate some analysis first!");
      }
    } finally {
      setIsLoading(false);
    }
  };

  const getSignalTypeColor = (type) => {
    return type === "BUY" ? "success" : "danger";
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 80) return "success";
    if (confidence >= 60) return "warning";
    return "danger";
  };

  const filteredSignals = signals.filter((signal) => {
    if (activeTab === "buy") return signal.signal_type === "BUY";
    if (activeTab === "sell") return signal.signal_type === "SELL";
    return true;
  });

  return (
