import React, { useState, useEffect } from "react";
import { useTradingStore, useUIStore } from "../store";

export default function Sidebar({ isOpen }) {
  const { selectedMode, setMode } = useTradingStore((s) => ({
    selectedMode: s.selectedMode,
    setMode: s.setMode,
  }));

  const navItems = [
    { id: "dashboard", label: "Dashboard", icon: "üìä", path: "/dashboard" },
    { id: "analysis", label: "Stock Analysis", icon: "üìà", path: "/analysis" },
    { id: "portfolio", label: "Portfolio", icon: "üíº", path: "/portfolio" },
    { id: "signals", label: "Trade Signals", icon: "üéØ", path: "/signals" },
    { id: "intraday", label: "Intraday", icon: "‚ö°", path: "/intraday" },
    { id: "paper", label: "Paper Trading", icon: "üìù", path: "/paper-trading" },
    { id: "alerts", label: "Smart Alerts", icon: "üîî", path: "/alerts" },
    { id: "risk", label: "Risk Manager", icon: "üõ°Ô∏è", path: "/risk" },
  ];

  return (
    <div className={`fixed inset-y-0 left-0 bg-dark text-white transition-all duration-300 ${isOpen ? "w-64" : "w-0"} overflow-hidden z-40 lg:static lg:w-64`}>
      {/* Header */}
      <div className="p-6 border-b border-gray-700">
        <h1 className="text-2xl font-bold">üöÄ KVK</h1>
        <p className="text-xs text-gray-400 mt-1">AI Trading Assistant</p>
      </div>

      {/* Mode Switcher */}
      <div className="p-4 border-b border-gray-700">
        <p className="text-xs text-gray-400 mb-2">TRADING MODE</p>
        <div className="flex gap-2">
          <button
            onClick={() => setMode("beginner")}
            className={`flex-1 py-2 px-3 rounded text-sm font-medium transition ${
              selectedMode === "beginner"
                ? "bg-primary text-white"
                : "bg-gray-700 text-gray-300 hover:bg-gray-600"
            }`}
          >
            Beginner
          </button>
          <button
            onClick={() => setMode("pro")}
            className={`flex-1 py-2 px-3 rounded text-sm font-medium transition ${
              selectedMode === "pro"
                ? "bg-secondary text-white"
                : "bg-gray-700 text-gray-300 hover:bg-gray-600"
            }`}
          >
            Pro
          </button>
        </div>
      </div>

      {/* Navigation */}
      <nav className="p-4 space-y-2">
        {navItems.map((item) => (
          <a
            key={item.id}
            href={item.path}
            className="flex items-center gap-3 px-4 py-3 rounded-lg hover:bg-gray-700 transition text-gray-300 hover:text-white"
          >
            <span className="text-xl">{item.icon}</span>
            <span className="font-medium">{item.label}</span>
          </a>
        ))}
      </nav>

      {/* Footer */}
      <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-700 bg-gray-900">
        <button className="w-full py-2 px-4 bg-red-600 text-white rounded-lg hover:bg-red-700 transition font-medium text-sm">
          Logout
        </button>
      </div>
    </div>
  );
}
