import React, { useState } from "react";
import { recommendationApi } from "../api/endpoints";
import toast from "react-hot-toast";

export default function StockAnalysis() {
  const AVAILABLE_STOCKS = ["INFY", "TCS", "HDFCBANK"];
  const TRADING_STYLES = {
    INTRADAY: { label: "âš¡ Intraday (Same Day)", desc: "VWAP focus, tight stops, quick profits" },
    SWING: { label: "ðŸ“ˆ Swing (2-5 Days)", desc: "EMA + patterns, medium-term trends" },
    POSITIONAL: { label: "ðŸ“Š Positional (Weeks)", desc: "Daily chart, trend structure" },
    INVESTMENT: { label: "ðŸ’¼ Investment (Months+)", desc: "200 EMA, long-term trends" },
  };

  const [selectedStock, setSelectedStock] = useState("INFY");
  const [tradingStyle, setTradingStyle] = useState("SWING");
  const [capital, setCapital] = useState("100000");
  const [analysis, setAnalysis] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState("overview");

  const handleAnalyze = async () => {
    if (!selectedStock) {
      toast.error("Please select a stock");
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await recommendationApi.generateRecommendation({
        stock_symbol: selectedStock,
        trading_style: tradingStyle,
        capital: parseFloat(capital),
      });

      setAnalysis({ ...response.data, timestamp: new Date().toLocaleString("en-IN") });
      toast.success(`Analysis complete for ${selectedStock}`);
    } catch (err) {
      const message = err.response?.data?.error || "Analysis failed";
      setError(message);
      toast.error(`âŒ ${message}`);
      console.error("Analysis error:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const getTrendExplanation = (trend) => {
    if (trend === "BULLISH") {
      return { text: "Uptrend", color: "text-green-600", icon: "ðŸ“ˆ", meaning: "Buyers in control" };
    } else if (trend === "BEARISH") {
      return { text: "Downtrend", color: "text-red-600", icon: "ðŸ“‰", meaning: "Sellers in control" };
    }
    return { text: "Sideways", color: "text-gray-600", icon: "â†”ï¸", meaning: "Mixed signals" };
  };

  const getRSIInterpretation = (rsi) => {
    if (rsi > 70) return { text: "Overbought", color: "text-red-600" };
    if (rsi < 30) return { text: "Oversold", color: "text-green-600" };
    return { text: "Neutral", color: "text-blue-600" };
  };

  return (
    <div className="space-y-6 p-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg p-6 shadow-lg">
        <h1 className="text-3xl font-bold">ðŸ“Š Stock Analysis</h1>
        <p className="text-gray-100 mt-2">Complete technical analysis with trading strategy</p>
      </div>

      {/* Setup Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-4">ðŸ” Analysis Setup</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-2">Select Stock</label>
            <select
              value={selectedStock}
              onChange={(e) => setSelectedStock(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            >
              {AVAILABLE_STOCKS.map((stock) => (
                <option key={stock} value={stock}>{stock}</option>
              ))}
            </select>
          </div>

          <div>
