import React, { useState } from "react";
import { stockApi, riskApi } from "../api/endpoints";
import toast from "react-hot-toast";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

export default function StockAnalysis() {
  const [symbol, setSymbol] = useState("INFY");
  const [analysis, setAnalysis] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleAnalyzeStock = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await stockApi.getStockAnalysis(symbol);
      setAnalysis(response.data);
      toast.success(`Analysis loaded for ${symbol}`);
    } catch (error) {
      toast.error("Failed to analyze stock");
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  const getTrendColor = (value) => {
    if (value > 0) return "text-green-600";
    if (value < 0) return "text-red-600";
    return "text-gray-600";
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-900">Stock Analysis</h1>
        <p className="text-gray-600 mt-2">Deep technical analysis with indicators</p>
      </div>

      {/* Search Form */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleAnalyzeStock} className="flex gap-4">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Stock Symbol
            </label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              placeholder="e.g., INFY, TCS"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>
          <div className="flex items-end">
            <button
              type="submit"
              disabled={isLoading}
              className="bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 px-6 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              {isLoading ? "Analyzing..." : "Analyze"}
            </button>
          </div>
        </form>
      </div>

      {/* Analysis Results */}
      {analysis && (
        <div className="space-y-6">
          {/* Stock Overview */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="bg-white rounded-lg shadow-md p-4">
              <p className="text-sm text-gray-600 mb-1">Current Price</p>
              <p className="text-3xl font-bold text-gray-900">‚Çπ{analysis.current_price}</p>
            </div>
            <div className="bg-white rounded-lg shadow-md p-4">
              <p className="text-sm text-gray-600 mb-1">Support Level</p>
              <p className="text-3xl font-bold text-blue-600">‚Çπ{analysis.support_level}</p>
            </div>
            <div className="bg-white rounded-lg shadow-md p-4">
              <p className="text-sm text-gray-600 mb-1">Resistance Level</p>
              <p className="text-3xl font-bold text-red-600">‚Çπ{analysis.resistance_level}</p>
            </div>
            <div className="bg-white rounded-lg shadow-md p-4">
              <p className="text-sm text-gray-600 mb-1">52W Range</p>
              <p className="text-lg font-bold text-gray-900">
                ${analysis.support_level?.toFixed(2)} - ${analysis.resistance_level?.toFixed(2)}
              </p>
            </div>
          </div>

          {/* Technical Indicators */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* RSI */}
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">RSI (14)</h3>
              <div className="space-y-3">
                <div>
                  <p className="text-sm text-gray-600">Current RSI</p>
                  <p className="text-4xl font-bold text-primary">{analysis.rsi?.toFixed(1)}</p>
                </div>
                <div className="bg-gray-100 rounded-full h-2 mt-4">
                  <div
                    className="bg-primary h-2 rounded-full transition-all"
                    style={{
                      width: `${Math.min(Math.max(analysis.rsi, 0), 100)}%`,
                    }}
                  />
                </div>
                <div className="flex justify-between text-xs text-gray-500">
                  <span>0</span>
                  <span>50 (Neutral)</span>
                  <span>100</span>
                </div>
                <p className="text-sm text-gray-600 mt-3">
                  {analysis.rsi > 70
                    ? "‚ö†Ô∏è Overbought - Potential sell signal"
                    : analysis.rsi < 30
                    ? "‚úÖ Oversold - Potential buy signal"
                    : "Neutral momentum"}
                </p>
              </div>
            </div>

            {/* Bollinger Bands */}
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Bollinger Bands</h3>
              <div className="space-y-3">
                <div>
                  <p className="text-sm text-gray-600">Upper</p>
                  <p className="text-2xl font-bold text-red-600">‚Çπ{analysis.bollinger_upper}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Middle (SMA 20)</p>
                  <p className="text-2xl font-bold text-primary">‚Çπ{analysis.bollinger_middle}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Lower</p>
                  <p className="text-2xl font-bold text-blue-600">‚Çπ{analysis.bollinger_lower}</p>
                </div>
              </div>
            </div>

            {/* Moving Averages */}
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Moving Averages</h3>
              <div className="space-y-3">
                <div>
                  <p className="text-sm text-gray-600">SMA 20</p>
                  <p className="text-2xl font-bold">‚Çπ{analysis.sma_20}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">SMA 50</p>
                  <p className="text-2xl font-bold">‚Çπ{analysis.sma_50}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">VWAP</p>
                  <p className="text-2xl font-bold text-primary">‚Çπ{analysis.vwap}</p>
                </div>
              </div>
            </div>
          </div>

          {/* Volume Analysis */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Volume & Trend Analysis</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <p className="text-sm text-gray-600 mb-2">Volume Trend</p>
                <p className="text-2xl font-bold">{analysis.volume_trend || "Normal"}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600 mb-2">Price Trend</p>
                <p className={`text-2xl font-bold ${analysis.trend === "UP" ? "text-green-600" : analysis.trend === "DOWN" ? "text-red-600" : "text-gray-600"}`}>
                  {analysis.trend || "Neutral"}
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-600 mb-2">Trend Probability</p>
                <p className="text-2xl font-bold text-primary">{analysis.trend_probability}%</p>
              </div>
            </div>
          </div>

          {/* Fibonacci Levels */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Fibonacci Retracement Levels</h3>
            <div className="space-y-2">
              {analysis.fibonacci_levels && analysis.fibonacci_levels.length > 0 ? (
                analysis.fibonacci_levels.map((level, idx) => (
                  <div key={idx} className="flex justify-between items-center py-2 border-b">
                    <span className="text-gray-600">{(level.ratio * 100).toFixed(1)}%</span>
                    <span className="font-semibold">‚Çπ{level.price?.toFixed(2)}</span>
                  </div>
                ))
              ) : (
                <p className="text-gray-500">Fibonacci levels being calculated...</p>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Empty State */}
      {!analysis && !isLoading && (
        <div className="bg-white rounded-lg shadow-md p-12 text-center">
          <p className="text-gray-600 text-lg">Enter a stock symbol to start analysis</p>
          <p className="text-gray-400">Try INFY, TCS, or HDFCBANK</p>
        </div>
      )}

      {/* Loading State */}
      {isLoading && (
        <div className="text-center py-12">
          <div className="inline-block animate-spin text-4xl">üìä</div>
          <p className="text-gray-600 mt-4">Analyzing {symbol}...</p>
        </div>
      )}
    </div>
  );
}
