import React, { useState, useEffect } from "react";
import { recommendationApi, stockApi, riskApi } from "../api/endpoints";
import toast from "react-hot-toast";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

export default function Dashboard() {
  const [symbol, setSymbol] = useState("INFY");
  const [tradingStyle, setTradingStyle] = useState("SWING");
  const [capital, setCapital] = useState(100000);
  const [isLoading, setIsLoading] = useState(false);
  const [recommendation, setRecommendation] = useState(null);

  const handleGenerateRecommendation = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await recommendationApi.generateRecommendation({
        stock_symbol: symbol,
        trading_style: tradingStyle,
        capital,
      });

      setRecommendation(response.data);
      toast.success("Recommendation generated!");
    } catch (error) {
      toast.error("Failed to generate recommendation");
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-900">Trading Dashboard</h1>
        <p className="text-gray-600 mt-2">
          <span className="inline-block px-3 py-1 bg-amber-100 text-amber-800 rounded-full text-sm font-semibold">
            ‚≠ê Pro+ Mode - Advanced Analysis & Full Features
          </span>
        </p>
      </div>

      {/* Input Form */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleGenerateRecommendation} className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Stock Symbol</label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              placeholder="e.g., INFY"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Trading Style</label>
            <select
              value={tradingStyle}
              onChange={(e) => setTradingStyle(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            >
              <option value="INTRADAY">Intraday</option>
              <option value="SWING">Swing</option>
              <option value="POSITIONAL">Positional</option>
              <option value="LONG_TERM">Long-Term</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Capital (‚Çπ)</label>
            <input
              type="number"
              value={capital}
              onChange={(e) => setCapital(parseInt(e.target.value))}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div className="flex items-end">
            <button
              type="submit"
              disabled={isLoading}
              className="w-full bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              {isLoading ? "Analyzing..." : "Analyze"}
            </button>
          </div>
        </form>
      </div>

      {/* Recommendation Display */}
      {recommendation && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Signal Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-green-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Trading Signal</h3>
            <div className="text-4xl font-bold text-green-600">{recommendation.signal}</div>
            <p className="text-gray-600 mt-2">Confidence: {recommendation.win_probability}%</p>
          </div>

          {/* Entry Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-blue-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Entry Price</h3>
            <div className="text-4xl font-bold text-blue-600">‚Çπ{recommendation.entry_price}</div>
            <p className="text-gray-600 mt-2">Risk: {recommendation.risk_amount}%</p>
          </div>

          {/* Stop Loss Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-red-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Stop Loss</h3>
            <div className="text-4xl font-bold text-red-600">‚Çπ{recommendation.stop_loss}</div>
            <p className="text-gray-600 mt-2">Risk/Reward: {recommendation.risk_reward_ratio}:1</p>
          </div>
        </div>
      )}

      {/* Pro Mode: Detailed Analysis */}
      {recommendation && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">‚≠ê Pro+ Technical Analysis</h2>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
            <div>
              <p className="text-sm text-gray-600">RSI (14)</p>
              <p className="text-2xl font-bold text-gray-900">{recommendation.rsi || "N/A"}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Bollinger Band Position</p>
              <p className="text-lg font-semibold text-gray-900">{recommendation.bollinger_position || "N/A"}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">VWAP</p>
              <p className="text-2xl font-bold text-gray-900">‚Çπ{recommendation.vwap || "N/A"}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Volume Trend</p>
              <p className="text-lg font-semibold text-green-600">{recommendation.volume_trend || "Normal"}</p>
            </div>
          </div>

          {/* Targets */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-3">üéØ Profit Targets</h3>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              {[recommendation.target_1, recommendation.target_2, recommendation.target_3, recommendation.target_4].map((target, idx) => (
                <div key={idx} className="bg-green-50 p-4 rounded-lg border-l-4 border-green-500">
                  <p className="text-sm text-gray-600">Target {idx + 1}</p>
                  <p className="text-2xl font-bold text-green-600">‚Çπ{target || "N/A"}</p>
                </div>
              ))}
            </div>
          </div>

          {/* Risk Assessment */}
          {recommendation.risk_assessment && (
            <div className="mt-6 pt-6 border-t">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">üõ°Ô∏è Risk Assessment</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div>
                  <p className="text-sm text-gray-600">Risk Level</p>
                  <p className={`text-lg font-bold ${
                    recommendation.risk_assessment.risk_level === "VERY_LOW" ? "text-green-600" :
                    recommendation.risk_assessment.risk_level === "LOW" ? "text-blue-600" :
                    recommendation.risk_assessment.risk_level === "MODERATE" ? "text-yellow-600" :
                    "text-red-600"
                  }`}>
                    {recommendation.risk_assessment.risk_level}
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Risk %</p>
                  <p className="text-lg font-bold text-gray-900">{recommendation.risk_assessment.risk_percentage?.toFixed(2)}%</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Volatility</p>
                  <p className="text-lg font-bold text-gray-900">{recommendation.risk_assessment.volatility_score?.toFixed(0)}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Max Position Size</p>
                  <p className="text-lg font-bold text-gray-900">{recommendation.risk_assessment.max_position_size}</p>
                </div>
              </div>
            </div>
          )}

          {/* Alternative Investments */}
          {recommendation.alternatives && recommendation.alternatives.length > 0 && (
            <div className="mt-6 pt-6 border-t">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">üí° Alternative Investments</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {recommendation.alternatives.slice(0, 2).map((alt, idx) => (
                  <div key={idx} className="bg-gradient-to-r from-blue-50 to-purple-50 p-4 rounded-lg">
                    <p className="font-semibold text-gray-900">{alt.name}</p>
                    <p className="text-sm text-gray-600 mt-1">{alt.investment_type}</p>
                    <div className="flex justify-between mt-2">
                      <span className="text-sm">Expected Return: <span className="font-bold text-green-600">{alt.expected_return}%</span></span>
                      <span className={`text-sm font-bold ${alt.risk_level === "LOW" ? "text-green-600" : alt.risk_level === "MODERATE" ? "text-yellow-600" : "text-red-600"}`}>
                        {alt.risk_level} Risk
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
