import api from "./client";

// Safe wrapper for API calls with error handling
async function safeCall(apiFunc) {
  try {
    const response = await apiFunc();
    if (response.data && response.data.success === false) {
      throw response;
    }
    return response;
  } catch (error) {
    if (!error.response) {
      error.response = {
        data: { error: "NETWORK_ERROR", message: "Network connection failed" },
        status: 0,
      };
    }
    throw error;
  }
}

export const authApi = {
  login: (username, password) =>
    safeCall(() => api.post("/api/auth/token/", { username, password })),

  logout: () => {
    localStorage.removeItem("access_token");
    localStorage.removeItem("refresh_token");
    localStorage.removeItem("user");
  },

  getUser: () => safeCall(() => api.get("/api/auth/user/")),
};

export const stockApi = {
  analyzeStock: (symbol) =>
    api.post("/api/stocks/", { symbol }),

  getStockAnalysis: (symbol) =>
    api.get(`/api/stocks/${symbol}/`),

  getStocks: () =>
    api.get("/api/stocks/"),
};

export const recommendationApi = {
  generateRecommendation: (data) =>
    api.post("/api/recommendations/generate/", data),

  getRecommendations: () =>
    api.get("/api/recommendations/"),

  getActiveSignals: () =>
    api.get("/api/recommendations/active/"),

  executeRecommendation: (id) =>
    api.post(`/api/recommendations/${id}/execute/`),
};

export const portfolioApi = {
  getPortfolios: () =>
    api.get("/api/portfolio/"),

  createPortfolio: (data) =>
    api.post("/api/portfolio/", data),

  getPortfolio: (id) =>
    api.get(`/api/portfolio/${id}/`),

  getPortfolioSummary: (id) =>
    api.get(`/api/portfolio/${id}/summary/`),

  addCapital: (id, amount) =>
    api.post(`/api/portfolio/${id}/add_capital/`, { amount }),
};

export const riskApi = {
  assessRisk: (data) =>
    api.post("/api/risk/assess/", data),

  getRiskAssessments: () =>
    api.get("/api/risk/"),
};

export const signalApi = {
  generateSignals: (data) =>
    api.post("/api/signals/", data),

  getSignals: () =>
    api.get("/api/signals/"),
};

export const intradayApi = {
  generateIntradaySignal: (data) =>
    api.post("/api/intraday-signals/", data),

  getIntradaySignals: () =>
    api.get("/api/intraday-signals/"),
};

export const paperTradingApi = {
  startPaperTrade: (data) =>
    api.post("/api/paper-trading/", data),

  getPaperTrades: () =>
    api.get("/api/paper-trading/"),

  closePaperTrade: (id) =>
    api.post(`/api/paper-trading/${id}/close/`),
};

export const alertApi = {
  createAlert: (data) =>
    api.post("/api/smart-alerts/", data),

  getAlerts: () =>
    api.get("/api/smart-alerts/"),

  deleteAlert: (id) =>
    api.delete(`/api/smart-alerts/${id}/`),
};

export const marketApi = {
  getMarketSummary: () =>
    api.get("/api/market-summary/"),

  getSupportResistance: (symbol) =>
    api.post("/api/support-resistance/", { symbol }),
};

export const investmentApi = {
  getInvestmentPlan: (data) =>
    api.post("/api/investment-planner/", data),

  getAlternatives: (data) =>
    api.post("/api/ai-explain/", data),
};

export const technicalAnalysisApi = {
  analyzeTechnical: (symbol, timeframe = "1D", market_open = true) =>
    api.post("/api/technical-analysis/analyze_technical/", {
      symbol,
      timeframe,
      market_open,
    }),

  getPatterns: (symbol) =>
    api.get(`/api/technical-analysis/patterns/${symbol}/`),
};

export const usersApi = {
  getProfile: () =>
    api.get("/api/users/profile/"),

  updateProfile: (data) =>
    api.put("/api/users/profile/", data),

  updateSettings: (data) =>
    api.put("/api/users/settings/", data),

  getSettings: () =>
    api.get("/api/users/settings/"),

  changePassword: (data) =>
    api.post("/api/users/change-password/", data),
};

export const tradingApi = {
  getPortfolios: () =>
    api.get("/api/portfolio/"),

  getRecommendations: () =>
    api.get("/api/recommendations/"),

  getActiveTrades: () =>
    api.get("/api/portfolio/?status=ACTIVE"),
};
