import React, { useState, useEffect } from "react";
import { intradayApi, recommendationApi } from "../api/endpoints";
import { useTradingStore } from "../store";
import toast from "react-hot-toast";
import { riskManagement, marketStatus, formatters } from "../utils/tradingEngine";
import { Card, CardHeader, CardTitle, CardContent } from "../components/ui/Card";
import { Button } from "../components/ui/Button";
import { Badge, Stat, Progress, Alert } from "../components/ui/Common";
import { Tabs } from "../components/ui/Layouts";

export default function Signals() {
  const [signals, setSignals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("all");
  const [selectedMode] = useTradingStore((s) => [s.selectedMode]);
  const [marketOpen, setMarketOpen] = useState(false);
  const [marketStatus_info, setMarketStatusInfo] = useState(null);
  const [defaultCapital, setDefaultCapital] = useState(100000);
  const [selectedSignal, setSelectedSignal] = useState(null);
  const [dataSource, setDataSource] = useState("LIVE");

  useEffect(() => {
    fetchSignals();
    const timer = setInterval(fetchSignals, 5 * 60 * 1000); // Refresh every 5 minutes
    return () => clearInterval(timer);
  }, [activeTab]);

  useEffect(() => {
    const updateMarketStatus = () => {
      const now = new Date();
      const hours = now.getHours();
      const minutes = now.getMinutes();
      const dayOfWeek = now.getDay();

      const isWeekday = dayOfWeek > 0 && dayOfWeek < 6;
      const isMarketHours = (hours > 9 || (hours === 9 && minutes >= 15)) &&
                            (hours < 15 || (hours === 15 && minutes <= 30));

      const holidays = [
        "2026-01-26", "2026-03-25", "2026-04-14", "2026-08-15",
        "2026-09-02", "2026-10-02", "2026-10-25", "2026-12-25",
      ];

      const today = now.toISOString().split("T")[0];
      const isHoliday = holidays.includes(today);

      let status = "CLOSED";
      let source = "HISTORICAL";

      if (isHoliday) {
        status = "HOLIDAY";
        source = "HISTORICAL";
      } else if (isWeekday && isMarketHours) {
        status = "OPEN";
        source = "LIVE";
      } else {
        status = "CLOSED";
        source = "HISTORICAL";
      }

      setMarketOpen(status === "OPEN");
      setMarketStatusInfo(status);
      setDataSource(source);
    };

    updateMarketStatus();
    const timer = setInterval(updateMarketStatus, 60000);
    return () => clearInterval(timer);
  }, []);

  const isValidSignal = (signal) => {
    // Filter out invalid or placeholder signals
    const entry = Number(signal.entry_price) || 0;
    const target = Number(signal.target_1) || 0;
    const stop = Number(signal.stop_loss) || 0;
    const confidence = Number(signal.confidence) || 0;

    // Must have valid prices
    if (!entry || !target || !stop) return false;

    // Entry must be between stop and target
    const validRange = (stop < entry && entry < target) || (target < entry && entry < stop);
    if (!validRange) return false;

    // Must have valid confidence
    if (confidence <= 0 || confidence > 100) return false;

    // Must have a signal type
    if (!signal.signal_type || !['BUY', 'SELL'].includes(signal.signal_type)) return false;

    return true;
  };

  const fetchSignals = async () => {
    setIsLoading(true);
    try {
      let response;
      try {
        response =
          activeTab === "intraday"
            ? await intradayApi.getIntradaySignals()
            : await recommendationApi.getActiveSignals();
      } catch (err) {
        console.error("API Error fetching signals:", err);

        if (err.response?.status === 404) {
          setSignals([]);
          return;
        } else if (err.response?.status === 401) {
          toast.error("Session expired. Please login again.");
        }

        setSignals([]);
        return;
      }

      const signalList = Array.isArray(response.data)
        ? response.data
        : response.data?.results || response.data?.data || [];

      // Filter only valid signals from today
      const validSignals = (signalList || []).filter(isValidSignal);

      const enhancedSignals = validSignals.map((signal) => {
        const entry = Number(signal.entry_price) || 0;
        const target = Number(signal.target_1) || 0;
        const stop = Number(signal.stop_loss) || 0;
        const confidence = Number(signal.confidence) || 0;

        // Calculate risk/reward ratio
        const riskAmount = Math.abs(entry - stop);
        const rewardAmount = Math.abs(target - entry);
        const riskReward = riskAmount > 0 ? rewardAmount / riskAmount : 0;

        return {
          ...signal,
          entry_price: entry,
          target_1: target,
          stop_loss: stop,
          confidence: Math.min(100, Math.max(0, confidence)),
          market_status: marketOpen ? "LIVE" : "HISTORICAL",
          riskReward: riskReward.toFixed(2),
          expectedProfit: ((rewardAmount / entry) * 100).toFixed(2),
          validated: true,
          timestamp: new Date().toLocaleString("en-IN"),
        };
      });

      setSignals(enhancedSignals);
    } finally {
      setIsLoading(false);
    }
  };

  const getSignalTypeColor = (type) => {
    return type === "BUY" ? "success" : "danger";
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 80) return "success";
    if (confidence >= 60) return "warning";
    return "danger";
  };

  const filteredSignals = signals.filter((signal) => {
    if (activeTab === "buy") return signal.signal_type === "BUY";
    if (activeTab === "sell") return signal.signal_type === "SELL";
    return true;
  });

  return (
    <div className="space-y-6 animate-fade-in-up">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Trade Signals</h1>
          <p className="text-gray-600 mt-1">AI-powered trading signals and recommendations</p>
        </div>
        <div className="flex gap-2">
          <Button onClick={fetchSignals} isLoading={isLoading} variant="primary">
            üîÑ Refresh
          </Button>
          <Button variant="secondary">üì• Export</Button>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Stat
          label="Total Signals"
          value={signals.length}
          change={`${signals.filter(s => s.signal_type === 'BUY').length} Buy`}
          icon="üìä"
        />
        <Stat
          label="Buy Signals"
          value={signals.filter(s => s.signal_type === "BUY").length}
          change={`${(signals.filter(s => s.signal_type === "BUY").length / signals.length * 100 || 0).toFixed(0)}%`}
          changeType="up"
          icon="üìà"
        />
        <Stat
          label="Sell Signals"
          value={signals.filter(s => s.signal_type === "SELL").length}
          change={`${(signals.filter(s => s.signal_type === "SELL").length / signals.length * 100 || 0).toFixed(0)}%`}
          changeType="down"
          icon="üìâ"
        />
        <Stat
          label="Avg Confidence"
          value={`${(signals.reduce((a, s) => a + (s.confidence || 0), 0) / signals.length || 0).toFixed(0)}%`}
          icon="üéØ"
        />
      </div>

      {/* Tabs */}
      <Tabs
        tabs={[
          { id: "all", label: `All Signals (${signals.length})`, icon: "üìä" },
          { id: "buy", label: `Buy (${signals.filter(s => s.signal_type === "BUY").length})`, icon: "üìà" },
          { id: "sell", label: `Sell (${signals.filter(s => s.signal_type === "SELL").length})`, icon: "üìâ" },
          { id: "intraday", label: "Intraday", icon: "‚ö°" },
        ]}
        activeTab={activeTab}
        onTabChange={setActiveTab}
      />

      {/* Signals List */}
      <div className="space-y-4">
        {isLoading ? (
          <Card className="text-center py-12">
            <p className="text-gray-600">Loading signals...</p>
          </Card>
        ) : filteredSignals.length > 0 ? (
          filteredSignals.map((signal, index) => (
            <Card
              key={index}
              className="hover:shadow-md transition cursor-pointer"
              onClick={() => setSelectedSignal(selectedSignal?.stock_symbol === signal.stock_symbol ? null : signal)}
            >
              <div className="grid grid-cols-1 lg:grid-cols-6 gap-4">
                {/* Stock Info */}
                <div className="lg:col-span-1">
                  <p className="text-xs text-gray-500 uppercase font-semibold">Stock</p>
                  <p className="text-2xl font-bold text-gray-900 mt-1">
                    {signal.stock_symbol}
                  </p>
                  <Badge variant={getSignalTypeColor(signal.signal_type)} className="mt-2">
                    {signal.signal_type}
                  </Badge>
                </div>

                {/* Price Details */}
                <div className="lg:col-span-2 grid grid-cols-3 gap-3">
                  <div>
                    <p className="text-xs text-gray-500 uppercase font-semibold">Entry</p>
                    <p className="text-lg font-bold text-gray-900 mt-1">
                      ‚Çπ{Number(signal.entry_price || 0).toFixed(2)}
                    </p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 uppercase font-semibold">Target</p>
                    <p className="text-lg font-bold text-green-600 mt-1">
                      ‚Çπ{Number(signal.target_1 || 0).toFixed(2)}
                    </p>
                    <p className="text-xs text-green-600 mt-0.5">
                      +{signal.expectedProfit}%
                    </p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 uppercase font-semibold">Stop</p>
                    <p className="text-lg font-bold text-red-600 mt-1">
                      ‚Çπ{Number(signal.stop_loss || 0).toFixed(2)}
                    </p>
                  </div>
                </div>

                {/* Confidence & Risk/Reward */}
                <div className="lg:col-span-2">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="text-xs text-gray-500 uppercase font-semibold mb-2">Confidence</p>
                      <Progress
                        value={signal.confidence || 0}
                        className="text-sm"
                      />
                    </div>
                    <div>
                      <p className="text-xs text-gray-500 uppercase font-semibold mb-1">R:R Ratio</p>
                      <p className="text-2xl font-bold text-blue-600">
                        1:{Number(signal.riskReward || 0).toFixed(2)}
                      </p>
                    </div>
                  </div>
                </div>

                {/* Actions */}
                <div className="lg:col-span-1 flex items-end justify-end gap-2">
                  <Button
                    size="sm"
                    variant="primary"
                    onClick={(e) => {
                      e.stopPropagation();
                      toast.success(`Added ${signal.stock_symbol} to watchlist`);
                    }}
                  >
                    ‚≠ê
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={(e) => {
                      e.stopPropagation();
                      toast.success(`Copied signal details`);
                    }}
                  >
                    üìã
                  </Button>
                </div>
              </div>

              {/* Expanded Details */}
              {selectedSignal?.stock_symbol === signal.stock_symbol && (
                <div className="mt-4 pt-4 border-t border-gray-200">
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    <div className="bg-gray-50 p-3 rounded-lg">
                      <p className="text-xs text-gray-600 font-medium">Time Horizon</p>
                      <p className="text-lg font-semibold text-gray-900 mt-1">
                        {signal.time_horizon || "Medium-term"}
                      </p>
                    </div>
                    <div className="bg-gray-50 p-3 rounded-lg">
                      <p className="text-xs text-gray-600 font-medium">Signal Type</p>
                      <p className="text-lg font-semibold text-gray-900 mt-1">
                        {signal.signal_type}
                      </p>
                    </div>
                    <div className="bg-gray-50 p-3 rounded-lg">
                      <p className="text-xs text-gray-600 font-medium">Market Status</p>
                      <Badge variant={signal.market_status === "LIVE" ? "success" : "warning"}>
                        {signal.market_status}
                      </Badge>
                    </div>
                  </div>
                </div>
              )}
            </Card>
          ))
        ) : (
          <Card className="text-center py-12">
            <p className="text-xl text-gray-600 mb-2">üì≠ No signals yet</p>
            <p className="text-gray-500">Generate some stock analyses to get trading signals</p>
            <Button className="mt-4" onClick={() => window.location.href = "/dashboard"}>
              Go to Dashboard
            </Button>
          </Card>
        )}
      </div>
    </div>
  );
}
