import React, { useState, useEffect } from "react";
import { recommendationApi, stockApi, riskApi } from "../../api/endpoints";
import { useTradingStore } from "../../store";
import toast from "react-hot-toast";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

export default function Dashboard() {
  const [symbol, setSymbol] = useState("INFY");
  const [tradingStyle, setTradingStyle] = useState("SWING");
  const [capital, setCapital] = useState(100000);
  const [isLoading, setIsLoading] = useState(false);
  const [recommendation, setRecommendation] = useState(null);
  const [mode] = useTradingStore((s) => [s.selectedMode]);

  const handleGenerateRecommendation = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await recommendationApi.generateRecommendation({
        stock_symbol: symbol,
        trading_style: tradingStyle,
        capital,
      });

      setRecommendation(response.data);
      toast.success("Recommendation generated!");
    } catch (error) {
      toast.error("Failed to generate recommendation");
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-900">Trading Dashboard</h1>
        <p className="text-gray-600 mt-2">
          Mode: <span className="font-semibold text-primary">{mode === "beginner" ? "Beginner (Simple Signals)" : "Pro (Detailed Analysis)"}</span>
        </p>
      </div>

      {/* Input Form */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleGenerateRecommendation} className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Stock Symbol</label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              placeholder="e.g., INFY"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Trading Style</label>
            <select
              value={tradingStyle}
              onChange={(e) => setTradingStyle(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            >
              <option value="INTRADAY">Intraday</option>
              <option value="SWING">Swing</option>
              <option value="POSITIONAL">Positional</option>
              <option value="LONG_TERM">Long-Term</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Capital (₹)</label>
            <input
              type="number"
              value={capital}
              onChange={(e) => setCapital(parseInt(e.target.value))}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
            />
          </div>

          <div className="flex items-end">
            <button
              type="submit"
              disabled={isLoading}
              className="w-full bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 rounded-lg hover:shadow-lg transition disabled:opacity-50"
            >
              {isLoading ? "Analyzing..." : "Analyze"}
            </button>
          </div>
        </form>
      </div>

      {/* Recommendation Display */}
      {recommendation && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Signal Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-green-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Trading Signal</h3>
            <div className="text-4xl font-bold text-green-600">{recommendation.signal}</div>
            <p className="text-gray-600 mt-2">Confidence: {recommendation.win_probability}%</p>
          </div>

          {/* Entry Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-blue-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Entry Price</h3>
            <div className="text-4xl font-bold text-blue-600">₹{recommendation.entry_price}</div>
            <p className="text-gray-600 mt-2">Risk: {recommendation.risk_amount}%</p>
          </div>

          {/* Stop Loss Card */}
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-red-500">
            <h3 className="text-sm font-medium text-gray-500 mb-2">Stop Loss</h3>
            <div className="text-4xl font-bold text-red-600">₹{recommendation.stop_loss}</div>
            <p className="text-gray-600 mt-2">Risk/Reward: {recommendation.risk_reward_ratio}:1</p>
          </div>
        </div>
      )}

      {/* Pro Mode: Detailed Analysis */}
      {mode === "pro" && recommendation && (
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Technical Analysis</h2>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
            <div>
              <p className="text-sm text-gray-600">RSI</p>
              <p className="text-2xl font-bold text-gray-900">{recommendation.rsi}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Bollinger Band Position</p>
              <p className="text-lg font-semibold text-gray-900">{recommendation.bollinger_position}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">VWAP</p>
              <p className="text-2xl font-bold text-gray-900">₹{recommendation.vwap}</p>
            </div>
            <div>
              <p className="text-sm text-gray-600">Volume Trend</p>
              <p className="text-lg font-semibold text-green-600">{recommendation.volume_trend}</p>
            </div>
          </div>

          {/* Targets */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-3">Profit Targets</h3>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              {recommendation.targets && recommendation.targets.map((target, idx) => (
                <div key={idx} className="bg-green-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-600">Target {idx + 1}</p>
                  <p className="text-2xl font-bold text-green-600">₹{target}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
