import React, { useState, useEffect } from "react";
import toast from "react-hot-toast";
import { technicalAnalysisApi } from "../api/endpoints";

export default function TechnicalPatternScanner() {
  const [symbol, setSymbol] = useState("INFY");
  const [analysis, setAnalysis] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("patterns");

  const handleScanStock = async (e) => {
    e?.preventDefault();
    setIsLoading(true);

    try {
      const response = await technicalAnalysisApi.analyzeTechnical(symbol);
      setAnalysis(response.data);
      toast.success(`Analysis complete for ${symbol}`);
    } catch (error) {
      toast.error("Failed to scan stock");
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    handleScanStock();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const PatternCard = ({ pattern, type = "bullish" }) => {
    const bgColor = type === "bullish" ? "bg-green-50 border-green-500" : "bg-red-50 border-red-500";
    const textColor = type === "bullish" ? "text-green-700" : "text-red-700";
    const icon = type === "bullish" ? "üìà" : "üìâ";

    return (
      <div className={`border-l-4 ${bgColor} p-4 rounded-lg mb-3`}>
        <div className="flex justify-between items-start">
          <div>
            <h4 className={`text-lg font-bold ${textColor}`}>{icon} {pattern.name}</h4>
            <p className="text-gray-700 mt-1">{pattern.signal}</p>
          </div>
          <span className={`text-sm font-bold px-3 py-1 rounded ${textColor} bg-opacity-10 bg-${type === 'bullish' ? 'green' : 'red'}-100`}>
            {pattern.confidence}% Confidence
          </span>
        </div>
      </div>
    );
  };

  const IndicatorDisplay = ({ indicators }) => {
    if (!indicators) return null;

    const getRSIColor = (rsi) => {
      if (rsi > 70) return "text-red-600";
      if (rsi < 30) return "text-green-600";
      return "text-yellow-600";
    };

    const getRSISignal = (rsi) => {
      if (rsi > 70) return "‚ö†Ô∏è Overbought";
      if (rsi < 30) return "‚úÖ Oversold";
      return "‚û°Ô∏è Neutral";
    };

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* RSI */}
        <div className="bg-white rounded-lg shadow p-4">
          <h4 className="font-bold text-gray-900 mb-3">RSI (14)</h4>
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-gray-600">Value:</span>
              <span className={`text-2xl font-bold ${getRSIColor(indicators.rsi)}`}>
                {indicators.rsi?.toFixed(1)}
              </span>
            </div>
            <div className="bg-gray-200 rounded-full h-2">
              <div
                className="bg-primary h-2 rounded-full"
                style={{ width: `${Math.min(100, indicators.rsi)}%` }}
              />
            </div>
            <p className={`text-sm font-semibold ${getRSIColor(indicators.rsi)}`}>
              {getRSISignal(indicators.rsi)}
            </p>
          </div>
        </div>

        {/* VWAP */}
        <div className="bg-white rounded-lg shadow p-4">
          <h4 className="font-bold text-gray-900 mb-3">VWAP</h4>
          <div className="space-y-2">
            <p className="text-2xl font-bold text-primary">
              ‚Çπ{indicators.vwap?.toFixed(2)}
            </p>
            <p className="text-sm text-gray-600">
              Volume Weighted Average Price
            </p>
            <p className="text-xs text-gray-500 mt-2">
              üí° Buy above VWAP (Intraday)
            </p>
          </div>
        </div>

        {/* EMA 20 & 50 */}
        <div className="bg-white rounded-lg shadow p-4">
          <h4 className="font-bold text-gray-900 mb-3">Moving Averages</h4>
          <div className="space-y-3">
            <div>
              <p className="text-sm text-gray-600">EMA 20:</p>
              <p className="text-xl font-bold text-blue-600">
                ‚Çπ{indicators.ema_20?.toFixed(2)}
              </p>
            </div>
            <div>
              <p className="text-sm text-gray-600">EMA 50:</p>
              <p className="text-xl font-bold text-purple-600">
                ‚Çπ{indicators.ema_50?.toFixed(2)}
              </p>
            </div>
          </div>
        </div>

        {/* Bollinger Bands */}
        <div className="bg-white rounded-lg shadow p-4">
          <h4 className="font-bold text-gray-900 mb-3">Bollinger Bands (20, 2)</h4>
          <div className="space-y-2">
            {indicators.bollinger_bands && (
              <>
                <div>
                  <p className="text-xs text-red-600">Upper:</p>
                  <p className="font-bold">‚Çπ{indicators.bollinger_bands.upper?.toFixed(2)}</p>
                </div>
                <div>
                  <p className="text-xs text-gray-600">Middle (SMA):</p>
                  <p className="font-bold">‚Çπ{indicators.bollinger_bands.middle?.toFixed(2)}</p>
                </div>
                <div>
                  <p className="text-xs text-blue-600">Lower:</p>
                  <p className="font-bold">‚Çπ{indicators.bollinger_bands.lower?.toFixed(2)}</p>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    );
  };

  const TradingLevels = ({ levels }) => {
    if (!levels) return null;

    return (
      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">üéØ Trading Levels</h3>

        {/* Support & Resistance */}
        <div className="mb-6">
          <h4 className="font-semibold text-gray-900 mb-3">Support & Resistance</h4>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-blue-50 p-4 rounded-lg border-l-4 border-blue-500">
              <p className="text-xs text-gray-600">Support</p>
              <p className="text-2xl font-bold text-blue-600">
                ‚Çπ{levels.support?.toFixed(2)}
              </p>
              <p className="text-xs text-gray-500 mt-1">Buying zone</p>
            </div>
            <div className="bg-red-50 p-4 rounded-lg border-l-4 border-red-500">
              <p className="text-xs text-gray-600">Resistance</p>
              <p className="text-2xl font-bold text-red-600">
                ‚Çπ{levels.resistance?.toFixed(2)}
              </p>
              <p className="text-xs text-gray-500 mt-1">Selling zone</p>
            </div>
          </div>
        </div>

        {/* Fibonacci Levels */}
        {levels.fibonacci && (
          <div>
            <h4 className="font-semibold text-gray-900 mb-3">üìä Fibonacci Retracement</h4>
            <div className="space-y-2 bg-gray-50 p-4 rounded-lg">
              {Object.entries(levels.fibonacci).map(([level, price]) => (
                <div key={level} className="flex justify-between items-center py-1 border-b">
                  <span className="text-sm text-gray-600">{level}</span>
                  <span className="font-semibold text-gray-900">‚Çπ{price?.toFixed(2)}</span>
                </div>
              ))}
            </div>
            <p className="text-xs text-gray-500 mt-2 italic">
              üí° Use Fibonacci levels for entry zones and profit targets
            </p>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-primary to-secondary rounded-lg shadow-md p-6 text-white">
        <h1 className="text-3xl font-bold">üîç Technical Pattern Scanner</h1>
        <p className="text-gray-100 mt-2">Auto-detect candlestick patterns, chart formations, and trading signals</p>
      </div>

      {/* Scanner Input */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleScanStock} className="flex gap-4">
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
            placeholder="Enter stock symbol (e.g., INFY)"
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary"
          />
          <button
            type="submit"
            disabled={isLoading}
            className="bg-gradient-to-r from-primary to-secondary text-white font-semibold py-2 px-6 rounded-lg hover:shadow-lg transition disabled:opacity-50"
          >
            {isLoading ? "Scanning..." : "Scan"}
          </button>
        </form>
      </div>

      {/* Tabs */}
      {analysis && (
        <>
          <div className="flex gap-2 bg-white rounded-lg shadow-md overflow-hidden">
            {[
              { key: "patterns", label: "üîπ Candlestick Patterns" },
              { key: "charts", label: "üìä Chart Patterns" },
              { key: "indicators", label: "üìà Indicators" },
              { key: "levels", label: "üéØ Trading Levels" }
            ].map((tab) => (
              <button
                key={tab.key}
                onClick={() => setActiveTab(tab.key)}
                className={`flex-1 py-3 px-4 font-medium transition ${
                  activeTab === tab.key
                    ? "bg-primary text-white"
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                }`}
              >
                {tab.label}
              </button>
            ))}
          </div>

          {/* Content */}
          <div className="bg-white rounded-lg shadow-md p-6">
            {activeTab === "patterns" && (
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-4">üîπ Candlestick Patterns</h2>
                {analysis.candlestick_patterns && analysis.candlestick_patterns.length > 0 ? (
                  analysis.candlestick_patterns.map((pattern, idx) => (
                    <PatternCard
                      key={idx}
                      pattern={pattern}
                      type={pattern.type === "Bearish" ? "bearish" : "bullish"}
                    />
                  ))
                ) : (
                  <p className="text-gray-500 text-center py-8">No significant candlestick patterns detected</p>
                )}
              </div>
            )}

            {activeTab === "charts" && (
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-4">üìä Chart Patterns</h2>
                {analysis.chart_patterns && analysis.chart_patterns.length > 0 ? (
                  analysis.chart_patterns.map((pattern, idx) => (
                    <PatternCard
                      key={idx}
                      pattern={pattern}
                      type={pattern.type === "Bearish" ? "bearish" : "bullish"}
                    />
                  ))
                ) : (
                  <p className="text-gray-500 text-center py-8">No chart patterns detected</p>
                )}
              </div>
            )}

            {activeTab === "indicators" && (
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-4">üìà Technical Indicators</h2>
                <IndicatorDisplay indicators={analysis.indicators} />
              </div>
            )}

            {activeTab === "levels" && (
              <div>
                <TradingLevels levels={analysis.trading_levels} />
              </div>
            )}
          </div>

          {/* Pattern Summary & Signal */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Bullish Signals */}
            <div className="bg-green-50 rounded-lg shadow-md p-6 border-l-4 border-green-500">
              <h3 className="text-lg font-bold text-green-800 mb-4">‚úÖ Bullish Signals</h3>
              <ul className="space-y-2">
                {analysis.candlestick_patterns
                  ?.filter((p) => p.type !== "Bearish")
                  .map((p, idx) => (
                    <li key={idx} className="flex items-center gap-2 text-green-700">
                      <span>‚úì</span> {p.name}
                    </li>
                  ))}
                {analysis.chart_patterns
                  ?.filter((p) => p.type === "Bullish")
                  .map((p, idx) => (
                    <li key={idx} className="flex items-center gap-2 text-green-700">
                      <span>‚úì</span> {p.pattern}
                    </li>
                  ))}
              </ul>
            </div>

            {/* Bearish Signals */}
            <div className="bg-red-50 rounded-lg shadow-md p-6 border-l-4 border-red-500">
              <h3 className="text-lg font-bold text-red-800 mb-4">‚ö†Ô∏è Bearish Signals</h3>
              <ul className="space-y-2">
                {analysis.candlestick_patterns
                  ?.filter((p) => p.type === "Bearish")
                  .map((p, idx) => (
                    <li key={idx} className="flex items-center gap-2 text-red-700">
                      <span>‚úó</span> {p.name}
                    </li>
                  ))}
                {analysis.chart_patterns
                  ?.filter((p) => p.type === "Bearish")
                  .map((p, idx) => (
                    <li key={idx} className="flex items-center gap-2 text-red-700">
